package com.example.matrice;
import android.graphics.drawable.Drawable;
import android.util.Base64;
import android.widget.ImageView;
import androidx.databinding.BindingAdapter;

import java.io.ByteArrayInputStream;

public class ChartElement
{
	private final static String txtImg = "";
	
	private String username;
	private String xp;
	private String hp;
	private Drawable img;
	
	public Drawable getImg() { return img; }
	public void setImg(Drawable img) { this.img = img; }
	public String getUsername() { return username; }
	public void setUsername(String username) { this.username=username; }
	public String getXp() { return xp; }
	public void setXp(String xp) { this.xp=xp; }
	public String getHp() { return hp; }
	public void setHp(String hp) { this.hp=hp; }
	
	public ChartElement()
	{
		username = "(no name)";
		xp = "0";
		hp = "100";
		img = Drawable.createFromStream(
				new ByteArrayInputStream(Base64.decode(txtImg, Base64.DEFAULT)),
				"defaultImg"
		);
		
	}
	
	@BindingAdapter({"srcCompat"})
	public static void setImageView(ImageView view, Drawable draw) {
		view.setImageDrawable(draw);
	}
	
}
